import PageTitle from "../pieces/PageTitle.vue";
import CoachesArray from "../../coaches";
export default {
  name: 'Coaches',
  components: {
    PageTitle: PageTitle
  },
  data: function data() {
    return {
      array: CoachesArray,
      interval: ''
    };
  },
  methods: {
    // DYNAMICALLY CREATING CONTENT - SLIDER
    createCoachBox: function createCoachBox(array, index) {
      var main = this.$refs.coachesContent;
      var circle = this.$refs.sliderCircle;
      main.innerHTML = '';
      var ci = document.createElement('div');
      ci.classList.add('coach-image');
      main.appendChild(ci);
      var img = document.createElement('img');
      img.src = array[index].url; // img

      ci.appendChild(img);
      var ch = document.createElement('div');
      ch.classList.add('coach-header');
      main.appendChild(ch);
      var ch_n = document.createElement('div');
      ch_n.classList.add('coach-header_name');
      ch.appendChild(ch_n);
      var ch_3 = document.createElement('h3');
      ch_3.innerHTML = array[index].name; // name

      ch_n.appendChild(ch_3);
      var ch_4 = document.createElement('h4');
      ch_4.innerHTML = array[index].description; // description

      ch_n.appendChild(ch_4);
      var ch_p = document.createElement('p');
      ch_p.classList.add('coach-header_quotation');
      ch_p.innerHTML = array[index].quotation; // quotation

      ch.appendChild(ch_p);
      var ca = document.createElement('div');
      ca.classList.add('coach-about');
      main.appendChild(ca);
      var ca_p = document.createElement('p');
      ca_p.innerHTML = array[index].about; // about

      ca.appendChild(ca_p);
      var d = circle.find(function (el) {
        return el.classList.contains('circle-active');
      });

      if (d) {
        d.classList.remove('circle-active');
        d.style.background = 'none';
      }

      circle[index].style.backgroundColor = '#000';
      circle[index].classList.add('circle-active');
      index === circle.length ? index = 0 : index++; // eslint-disable-line
    },
    // SLIDE ANIMATION
    slideEffect: function slideEffect(array, index) {
      var _this = this;

      var main = this.$refs.coachesContent;
      main.style.opacity = 0;
      main.style.transform = 'translateX(-300px)';
      setTimeout(function () {
        _this.createCoachBox(array, index);
      }, 500);
      setTimeout(function () {
        main.style = '';
      }, 500);
    },
    // EVENT WRAPPER
    slider: function slider(index, time) {
      var _this2 = this;

      var pom = arguments.length > 2 && arguments[2] !== undefined ? arguments[2] : 0;

      if (event) {
        var ev = event.target;
        var id = this.$refs.sliderCircle.indexOf(ev);
        clearInterval(this.interval);
        var main = this.$refs.coachesContent;
        main.style = '';
        this.slideEffect(this.array.CoachesArray, id);
        id === this.array.CoachesArray.length - 1 ? id = 0 : id++; // eslint-disable-line

        this.interval = setInterval(function () {
          _this2.slideEffect(_this2.array.CoachesArray, id);

          id === _this2.array.CoachesArray.length - 1 ? id = 0 : id++; // eslint-disable-line
        }, time);
        return;
      }

      this.slideEffect(this.array.CoachesArray, pom);
      pom === this.array.CoachesArray.length - 1 ? pom = 0 : pom++; // eslint-disable-line

      this.interval = setInterval(function () {
        _this2.slideEffect(_this2.array.CoachesArray, pom);

        pom === _this2.array.CoachesArray.length - 1 ? pom = 0 : pom++; // eslint-disable-line
      }, time);
    }
  },
  mounted: function mounted() {
    this.slider(0, 5000);
  },
  created: function created() {},
  destroyed: function destroyed() {}
};
