import { setTimeout } from 'timers';
export default {
  name: 'LoadingScreen',
  methods: {
    // Hiding loading screen
    hideLoadingScreen: function hideLoadingScreen(time) {
      var ls = document.querySelector('.loading-screen');
      setTimeout(function () {
        ls.parentNode.removeChild(ls);
      }, time * 2);
    },
    // Scrolling to the top
    scrollController: function scrollController(time) {
      return setTimeout(function () {
        window.scrollTo(0, 0);
      }, time);
    }
  },
  data: function data() {
    return {
      time: 2500
    };
  },
  mounted: function mounted() {
    this.scrollController(this.time);
    this.hideLoadingScreen(this.time);
  }
};
