.service, .service-reverse{
    display: grid;
    grid-template-columns: 1fr;

    align-items: center;

    margin: 0;

    .service_content{
        display: flex;
        flex-flow: column;
        text-align: center;

        margin: 10px 0;
        padding: 5px;

        .service_content__offer{
            display: flex;
            flex-flow: row;
            width: 100%;

            align-items: center;
            justify-content: space-around;

            .offer{
                display: flex;
                flex-flow: column;

                max-width: 100px;
                padding: 5px;
                border: 1px solid #000;
            }
        }
    }

    .service_image{
        display: flex;
        img{
            height: auto;
            width: 100%;
        }
    }
}

@media (min-width: 768px){
    .service{
        grid-template-columns: 4fr 6fr;
    }

    .service-reverse{
        grid-template-columns: 6fr 4fr;

        .service_content{
            grid-area: 1 / 2 / 1 / 2;
        }

        .service_image{
            grid-area: 1 / 1 / 1 / 1;
        }
    }

    .service, .service-reverse{
        .service_content{
            margin: 0 10px;
        }
        .service_image{
            height: 100%;

            img{
                height: 100%;
            }
        }
    }
}
