.coaches-content{
    display: grid;

    grid-template-columns: 1fr 2fr;

    margin: 5px;

    transform: translateX(0px);
    opacity: 1;

    transition: all .5s ease-in-out;

    height: 300px;
    width: 100%;

    .coach-image{

        align-items: center;
        justify-content: flex-start;

        grid-area: 1 / 1 / 1 / 1;

        align-items: center;
        justify-content: center;

        img{
            width: 100px;
            height: 100px;

            border-radius: 50%;
        }
    }

    .coach-header{
        grid-area: 1 / 2 / 1 / span 2;

        margin: 10px 5px;
        padding: 0 20px;

        .coach-header_name{
            position: relative;
            h3{
                font-size: 1.5em;
                font-weight: 400;
                padding: 0;
                margin: 0;
            }
            h4{
                font-size: 1em;
                font-weight: 300;
                padding: 0;
                margin: 0;
            }
            &::after{
                content: '';
                position: absolute;
                margin: 5px;

                width: 100%;
                height: 1px;

                background-color: #000;
            }
        }
         .coach-header_quotation{
            font-size: .8em;
            font-weight: 300;
            text-align: center;
        }
    }

    .coach-about{
            grid-area: 2 / 1 / 2 / span 3;

            max-width: 100%;
            margin: 5px;
            p{
                text-align: center;
                padding: 0 5px;
                font-size: 1em;
                font-weight: 400;
            }
        }
}

.coaches-slider{

        width: 100%;
        height: auto;

        display: flex;
        flex-flow: row;

        align-items: center;
        justify-content: center;

        margin: 10px 0;

        .coaches-slider_circle{
            width: 15px;
            height: 15px;

            border-radius: 50%;
            margin-left: 10px;

            background: none;
            border: 2px solid #000;

            transition: all .15s ease-in-out;
            &:hover{
                background-color: #000;
                cursor: pointer;
            }
        }
    }

.slider-before{
    display: grid;
    grid-template-columns: 1fr 2fr;
    margin: 5px;
    width: 100%;

    transition: all .5s ease-in-out;
}

@media (max-width: 768px){
    .coach-image{
        display: flex;

        align-items: center;
        justify-content: flex-end;
    }
}

@media (min-width: 768px){
    #coaches{
        background-image: url('../../../assets/s2_bg.png');
        background-size: 25%;
        background-repeat: no-repeat;
        background-position: 5% 10%;
    }
    .coaches-content{
        margin: 20px 0;

        position: relative;
        left: 35%;
        width: 60%;

        grid-template-columns: 1fr 2fr;
        .coach-image{
            grid-area: 1 / 1 / 1 / span 2;
            img{
                width: 150px;
                height: auto;
            }
        }
        .coach-header{
            grid-area: 1 / 2 / 1 / span 2;

            .coach-header_quotation{
              grid-area: 3 / 1 / 3 / 1;

              font-size: 1em;
              font-weight: 400;
            }
        }

        .coach-about{
            grid-area: 2 / 1 / span 2 / span 3;
        }
    }
}

@media (min-width: 1024px){
    .coaches-content{
        .coach-image{
            img{
                width: 200px;
            }
        }
    }
}
